func solve(data string) (int, int) {
	floor := 0
	basement := -1
	for i, ch := range data {
		if ch == '(' {
			floor += 1
		} else {
			floor -= 1
		}
		if basement == -1 && floor == -1 {
			basement = i + 1
		}
	}
	return floor, basement
}
