#!/usr/bin/env lua

-- advent of code 2015 day 1
--[[
part 1: ( up 1 floor ) down 1 floor, find the count give the input
part 2: given start at floor 1 what is the position of the instruction when on floor -1
]]--
function count_floor(data)
    local pcount = 0
    for i = 1,#data do
        ch = data:sub(i,i)
        if ch == '(' then pcount=pcount+1
        elseif ch == ')' then pcount=pcount-1
        end
    end
    return pcount
end

function at_floor(input, goal)
    goal = goal or -1
    local floor = 0
    for i = 1,#input do
        ch = input:sub(i,i)
        if ch == '(' then floor = floor + 1
        elseif ch == ')' then floor = floor - 1
        end
        if floor == goal then return i end
    end
end

